import{_ as e,c as o,ac as s,o as a}from"./chunks/framework.Ca0cswtj.js";const m=JSON.parse('{"title":"宾语","description":"","frontmatter":{"title":"宾语"},"headers":[],"relativePath":"documents/grammar/sentence/basic-element/object.md","filePath":"documents/grammar/sentence/basic-element/object.md","lastUpdated":1736074850000}'),c={name:"documents/grammar/sentence/basic-element/object.md"};function n(r,t,l,p,d,u){return a(),o("div",null,t[0]||(t[0]=[s('<h1 id="宾语-·-object" tabindex="-1">宾语 · Object <a class="header-anchor" href="#宾语-·-object" aria-label="Permalink to &quot;宾语 · Object&quot;">​</a></h1><p><strong>宾语</strong> 是句子中表示动作的接受者的部分，通常跟在及物动词后面。<br><strong>宾语</strong> 可以是 <code>人</code>、<code>事物</code>、<code>地方</code>、<code>抽象概念</code> 等。</p><ul><li><strong>作用</strong>：宾语是谓语动词作用的对象，它回答 <code>什么</code> 或 <code>谁</code> 的问题。</li></ul><hr><p><strong>类型</strong>：</p><ul><li><strong>直接宾语</strong>：直接受到动词的影响，通常回答 <code>什么</code> 或 <code>谁</code> 的问题。</li></ul><div class="info custom-block"><p class="custom-block-title">直接宾语：</p><p>She is reading <span class="text-red-600">a book</span>.<br> 她正在读 <span class="text-red-600">一本书</span>。</p><p>&quot;a book&quot; 是 直接宾语，回答了 &quot;她在读什么&quot; 的问题。</p><hr><p>I saw <span class="text-red-600">him</span> yesterday.<br> 我昨天看见了 <span class="text-red-600">他</span>。</p><p>&quot;him&quot; 是 直接宾语，回答了 &quot;我昨天看见了谁&quot; 的问题。</p></div><ul><li><strong>间接宾语</strong>：通常表示受益人或接受者，通常回答 <code>给谁</code> 或 <code>向谁</code> 的问题。</li></ul><div class="info custom-block"><p class="custom-block-title">间接宾语：</p><p>I gave <span class="text-red-600">her</span> <span class="text-yellow-600">a gift</span>.<br> 我给了 <span class="text-red-600">她</span> <span class="text-yellow-600">一份礼物</span>。</p><p>&quot;her&quot; 是 间接宾语，表示 &quot;给谁&quot;。<br> &quot;a gift&quot; 是 直接宾语，表示 &quot;给什么&quot;。</p><hr><p>She sent <span class="text-red-600">me</span> <span class="text-yellow-600">an email</span>.<br> 她发给 <span class="text-red-600">我</span> <span class="text-yellow-600">一封邮件</span>。</p><p>&quot;me&quot; 是 间接宾语，表示 &quot;给谁&quot;。<br> &quot;an email&quot; 是 直接宾语，表示 &quot;发了什么&quot;。</p></div>',9)]))}const b=e(c,[["render",n]]);export{m as __pageData,b as default};
