import{_ as l,c as o,ae as r,j as e,a as t,o as i}from"./chunks/framework.BAg1NFzN.js";const g=JSON.parse('{"title":"基本语序","description":"","frontmatter":{"title":"基本语序"},"headers":[],"relativePath":"documents/grammar/syntax/syntactic-rules/basic-word-order.md","filePath":"documents/grammar/syntax/syntactic-rules/basic-word-order.md","lastUpdated":1736524948000}'),s={name:"documents/grammar/syntax/syntactic-rules/basic-word-order.md"};function n(c,a,d,u,b,p){return i(),o("div",null,a[0]||(a[0]=[r('<h1 id="基本语序" tabindex="-1">基本语序 <a class="header-anchor" href="#基本语序" aria-label="Permalink to &quot;基本语序&quot;">​</a></h1><p><strong>基本语序（Basic Word Order）</strong> 是指构成一个完整句子时，单词和短语的排列顺序。</p><div class="warning custom-block"><p class="custom-block-title">注意：</p><p>本章出现的 <code>谓语</code> 概念，特指具有 <code>动作</code> 或 <code>行为</code> 含义的 <code>动词</code> 作为 <code>谓语</code> 时的状况。</p></div><h2 id="主语-谓语" tabindex="-1">主语 + 谓语 <a class="header-anchor" href="#主语-谓语" aria-label="Permalink to &quot;主语 + 谓语&quot;">​</a></h2><p><strong>Subject + Verb</strong><br><strong>主谓（SV）</strong></p><ul><li>主语，动作的执行者 和 承受者。</li><li>谓语，表示主语的动作。</li></ul><p>这种结构通常用于表达简单的句子，尤其在没有 <code>宾语</code> 的情况下。</p>',7),e("div",{class:"example"},[e("p",{class:"example-title"},[e("span",{text:"blue-500"},"主语"),t(" · "),e("span",{text:"red-600"},"谓语")]),e("hr"),e("p",null,[e("span",{text:"blue-500"},"I"),t(),e("span",{text:"red-600"},"work"),t(" every day."),e("br"),t(" 我每天工作。")]),e("hr"),e("p",null,[e("span",{text:"blue-500"},"They"),t(),e("span",{text:"red-600"},"are running"),t("."),e("br"),t(" 他们正在跑步。")]),e("hr"),e("p",null,[e("span",{text:"blue-500"},"Lucy"),t(),e("span",{text:"red-600"},"will study"),t(" tonight."),e("br"),t(" 露西今晚将会学习。")])],-1),r('<h2 id="主语-系动词-表语‌" tabindex="-1">主语 + 系动词 + 表语‌ <a class="header-anchor" href="#主语-系动词-表语‌" aria-label="Permalink to &quot;主语 + 系动词 + 表语‌&quot;">​</a></h2><p><strong>Subject + Linking Verb + Predicative</strong><br><strong>主系表（SVP）</strong></p><ul><li>主语，子中的主题或谈论的对象。</li><li>系动词，表示主语与表语之间的关系。</li><li>表语，描述主语的状态、特征或身份。</li></ul><h2 id="主语-谓语-宾语" tabindex="-1">主语 + 谓语 + 宾语 <a class="header-anchor" href="#主语-谓语-宾语" aria-label="Permalink to &quot;主语 + 谓语 + 宾语&quot;">​</a></h2><p><strong>Subject + Verb + Object</strong><br><strong>主谓宾（SVO）</strong></p><ul><li>主语，动作的执行者。</li><li>谓语，表示主语的动作或行为。</li><li>宾语，表示动作的承受者或受影响的对象。</li></ul><h2 id="主语-谓语-间接宾语-直接宾语" tabindex="-1">主语 + 谓语 + 间接宾语 + 直接宾语 <a class="header-anchor" href="#主语-谓语-间接宾语-直接宾语" aria-label="Permalink to &quot;主语 + 谓语 + 间接宾语 + 直接宾语&quot;">​</a></h2><p><strong>Subject + Verb + Indirect Object + Direct Object</strong><br><strong>主谓双宾（SVID）</strong></p><ul><li>主语，执行动作的主体。</li><li>谓语，表示主语的动作或行为。</li><li>间接宾语，是动作的受益者或接收者，通常表示 &quot;给&quot; 谁、&quot;对&quot; 谁等。</li><li>直接宾语，是动作直接作用的对象，通常表示被 &quot;给予&quot; 的物品、信息或内容。</li></ul><h2 id="主语-谓语-宾语-宾语补足语‌" tabindex="-1">主语 + 谓语 + 宾语 + 宾语补足语‌ <a class="header-anchor" href="#主语-谓语-宾语-宾语补足语‌" aria-label="Permalink to &quot;主语 + 谓语 + 宾语 + 宾语补足语‌&quot;">​</a></h2><p><strong>Subject + Verb + Object + Object Complement</strong><br><strong>主谓宾宾补（SVOO）</strong></p><ul><li>主语，执行动作的主体。</li><li>谓语，表示主语的动作或行为。</li><li>宾语，表示动作的承受者或受影响的对象。</li><li>宾语补足语，用于补充说明宾语的状态、性质或身份。</li></ul>',12)]))}const m=l(s,[["render",n]]);export{g as __pageData,m as default};
